mapper.select:
select raceinfo.check_onoff 

from airquay.raceinfo

where raceinfo.race_num = '${value}';

select pastHour, pastMinute, pastSecond, pastMiliSecond 

from ison

where ison.race_num='${race_num}';

select pastHour

from ison

where ison.race_num='${value}';

select pastMinute

from ison

where ison.race_num='${value}';

select pastSecond

from ison

where ison.race_num='${value}';

select pastMiliSecond

from ison

where ison.race_num='${value}';

select raceinfo.race_num, raceinfo.day_race_num, raceinfo.event_name, raceinfo.race_date, raceinfo.round_type, raceinfo.start_time, raceinfo.stop_time, raceinfo.check_onoff, raceinfo.progression
from airquay.raceinfo
where raceinfo.race_date = '${value}';

select rec.record_num, rec.race_num, rec.team_num, rec.bow_num, rec.500_time as fivehundred_time, rec.500_rank as fivehundred_rank, rec.1000_time as thousand_time, rec.1000_rank as thousand_rank, rec.1500_time as thousandfivehundred_time, rec.1500_rank as thousandfivehundred_rank, rec.finish_time, rec.finish_rank, ti.team_name, ti.team_country
from airquay.record rec left join airquay.team_info ti on ti.team_num = rec.team_num
where rec.race_num = '${race_num}';

mapper.update:
update raceinfo set 

check_onoff='${check_onoff}'

where raceinfo.race_num='${race_num}';

update ison set
pastHour='${pastHour}', pastMinute='${pastMinute}', pastSecond='${pastSecond}', pastMiliSecond='${pastMiliSecond}'
where ison.race_num='${race_num}';

update raceinfo set
start_time='${start_time}', check_onoff='${check_onoff}'
where raceinfo.race_num='${race_num}';

update raceinfo set
stop_time='${stop_time}', check_onoff='${check_onoff}'
where raceinfo.race_num='${race_num}';

mapper.select:
select raceinfo.start_time

from airquay.raceinfo

where raceinfo.race_num='${race_num}';

select raceinfo.stop_time

from airquay.raceinfo

where raceinfo.race_num='${race_num}';

select raceinfo.day_race_num
from airquay.raceinfo
where race_num='${value}';

select record.500_time
from airquay.record
where race_num='${value}' and bow_num='1';

select record.500_time
from airquay.record
where race_num='${value}' and bow_num='2';

select record.500_time
from airquay.record
where race_num='${value}' and bow_num='3';

select record.500_time
from airquay.record
where race_num='${value}' and bow_num='4';

select record.500_time
from airquay.record
where race_num='${value}' and bow_num='5';

select record.500_time
from airquay.record
where race_num='${value}' and bow_num='6';

select race_date
from airquay.raceinfo
where race_date='${value}';
